bool Users::validateJsonForCreation(const Json::Value &pJson, std::string &err)
{
    if (pJson.isMember("_id"))
    {
        if (!validJsonOfField(0, "_id", pJson["_id"], err, true))
            return false;
    }
    if (pJson.isMember("name"))
    {
        if (!validJsonOfField(1, "name", pJson["name"], err, true))
            return false;
    }
    else
    {
        err = "The name column cannot be null";
        return false;
    }
    if (pJson.isMember("email"))
    {
        if (!validJsonOfField(2, "email", pJson["email"], err, true))
            return false;
    }
    else
    {
        err = "The email column cannot be null";
        return false;
    }
    if (pJson.isMember("password"))
    {
        if (!validJsonOfField(3, "password", pJson["password"], err, true))
            return false;
    }
    else
    {
        err = "The password column cannot be null";
        return false;
    }
    if (pJson.isMember("updated_at"))
    {
        if (!validJsonOfField(4, "updated_at", pJson["updated_at"], err, true))
            return false;
    }
    if (pJson.isMember("created_at"))
    {
        if (!validJsonOfField(5, "created_at", pJson["created_at"], err, true))
            return false;
    }
    return true;
}
